import React,{useState , useEffect} from 'react'
import { Link,useNavigate } from 'react-router-dom'

function Form() {
  
  const navigate=useNavigate()
  const [user, setUser] = useState({
    name: "",
    email: "",
    password: ""
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser(prevUser => ({
      ...prevUser,
      [name]: value
    }));
    setErrors(prevErrors => ({
      ...prevErrors,
      [name]: ''
    }));
  };


  
  const handleValidation = () => {
    let errors = {};

    if (!user.name) {
      errors.name = "Please enter your name";
      alert("please enter your name")
    } else if (user.name.length < 3) {
      errors.name = "Name should be at least 3 characters long";
      alert("name should be al last 3 character long")
    } else if (!isNaN(user.name)) {
      errors.name = "Name should contain at least one character";
      alert("don't add numbers")
    }

    if (!user.password) {
      errors.password = "Please enter your password";
    } else if (user.password.length < 3) {
      errors.password = "Password should be at least 3 characters long";
      
    } 


    setErrors(errors);
  };


  
  const addUserdata = async (e) => {
    handleValidation();
    e.preventDefault();
 

    try {
      const {name, email, password } = user;
  
      if (!name || !email || !password) {
  
        alert("missing required  fields")
       
      }else{
        const data = await fetch("/Signup", {
          method: "POST",
          headers: {
              "Content-Type": "application/json"
          },
          body: JSON.stringify({
              name, email, password
          })
      });
    
      const res = await data.json();
      // console.log(res.status);
    
      if (res.status === 201) {
        alert("check your form ")
      
      }else{
        navigate("/")
      }
  
      }
    
    } catch (error) {
      
      console.log(error)
    }
 
   
    
  
}
  return (
    <div>

      <form action="">
<input type="text" name="" id="" />
<br />
<input type="text" name="" id="" />
<br />
<input type="text" name="" id="" />
<br />
<button onClick={addUserdata}>sigin</button>
      </form>
    </div>
  )
}

export default Form
