import React,{useState,useContext} from 'react'
import { Link,useNavigate } from 'react-router-dom'
import { UserContext } from '../App';
import "./style/login.css"

function Login() {
  const {state , dispatch}= useContext(UserContext);
  const navigate=useNavigate()
  const [user, setUser] = useState({

    email: "",
    password: ""
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser(prevUser => ({
      ...prevUser,
      [name]: value
    }));
    setErrors(prevErrors => ({
      ...prevErrors,
      [name]: ''
    }));
  };


  
  const handleValidation = () => {
    let errors = {};

    // if (!user.name) {
    //   errors.name = "Please enter your name";
    //   alert("please enter your name")
    // } else if (user.name.length < 3) {
    //   errors.name = "Name should be at least 3 characters long";
    //   alert("name should be al last 3 character long")
    // } else if (!isNaN(user.name)) {
    //   errors.name = "Name should contain at least one character";
    //   alert("don't add numbers")
    // }

    if (!user.password) {
      errors.password = "Please enter your password";
    } else if (user.password.length < 3) {
      errors.password = "Password should be at least 3 characters long";

      console.log(errors.password )
      
    } 


    setErrors(errors);
  };


  
  const addUserdata = async (e) => {
    handleValidation();
    e.preventDefault();
 
 
    try {
      const { email, password } = user;
  
      if ( !email || !password) {
  
        // alert("missing required  fields")
        alert("missing")
        // throw new Error('Missing required fields');
      }else{
  
        
      const data = await fetch("/signin", {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
           email, password
        })
    });
  
    const res = await data.json();
    // console.log(res.status);
  
    if (res.status === 201) {
      navigate("/")
      console.log(res.result.data)
       localStorage.setItem("usersdatatoken",res.result.token);
       dispatch({type:'user' , payload:true})
    
    }else{
     console.log("check your form")

    
    }
    
      }
    } catch (error) {
      
      console.log(error)
    }
   
  
}

  return (
    <div>

<div className='login-background'>

  <div className='loginflex'>

<div className='login'>
  <form>
  <center>
   
<h1>create a new login</h1>

<input type="email" name="email" value={user.email} onChange={handleChange} placeholder=" email" />
<br/>

<br/>
<br/>
<input type="password" name="password" value={user.password} onChange={handleChange} placeholder=" password"  
          style={{ borderColor: errors.password && "red" }}/>
       {/* // {errors.password && <p>{errors.password}</p>} */}
<br/>

<br/>
<br/>

<button onClick={addUserdata}>sigin</button>
<br />
<Link to="/">form</Link>
    </center>
</form>
</div>


  </div>
</div>

    </div>
  )
}

export default Login
